mapper.select:

	INNER JOIN equip_group_relation	EGR ON EGR.group_recid = EG.recid 
	INNER JOIN equip_info			EI ON EI.recid = EGR.equip_recid
WHERE EI.recid = #{equipId}

mapper.insert:
INSERT INTO equip_group_info (
	group_name, group_path, parent_group_recid,
	create_dt, create_ip, create_user
)
VALUES (
	#{groupName} ,#{groupPath}, #{parentGroupRecid}
	, LOCALTIMESTAMP, #{createIp}, #{createUser}
)

mapper.update:
UPDATE equip_group_info SET
	group_name = #{groupName}
	, group_path = #{groupPath}
	, update_dt = LOCALTIMESTAMP
	, update_ip = #{updateIp}
	, update_user = #{updateUser}
 WHERE recid = #{recid}

UPDATE equip_group_info SET
	group_name = #{groupName}
	, group_path = #{groupPath}
	, parent_group_recid = #{parentGroupRecid}
	, update_dt = LOCALTIMESTAMP
	, update_ip = #{updateIp}
	, update_user = #{updateUser}
WHERE recid = #{recid}

WITH RECURSIVE subgroups AS ( SELECT recid, group_path FROM equip_group_info WHERE recid=#{recid}
UNION ALL
SELECT A.recid, A.group_path FROM equip_group_info A INNER JOIN subgroups B ON A.parent_group_recid = B.recid ) UPDATE equip_group_info SET group_path = #{newGroupPath} || substring(group_path, length(#{oldGroupPath}) + 1) WHERE recid IN ( SELECT recid FROM subgroups )
