mapper.select:

	INNER JOIN user_group_relation	UGR ON UGR.group_recid = UG.recid
	INNER JOIN user_info			UI ON UI.recid = UGR.user_recid
WHERE UI.recid = #{recid}


	INNER JOIN user_group_relation	UGR ON UGR.group_recid = UG.recid
	INNER JOIN user_info			UI ON UI.recid = UGR.user_recid
WHERE UI.user_id = #{userId}

WITH RECURSIVE result_sub_group AS ( SELECT recid, group_name, group_path, parent_group_recid FROM user_group_info WHERE 1=1
AND parent_group_recid = #{groupRecid}
UNION ALL SELECT A.recid, A.group_name, A.group_path, A.parent_group_recid FROM user_group_info A JOIN result_sub_group B ON A.parent_group_recid = B.recid ) SELECT * FROM ( SELECT A.recid, A.group_name, A.group_path, A.parent_group_recid FROM result_sub_group A
UNION ALL SELECT B.recid, B.group_name, B.group_path, B.parent_group_recid FROM user_group_info B WHERE B.recid = #{groupRecid}
) N ORDER BY N.recid ASC

WITH RECURSIVE result_sub_group AS ( SELECT recid, group_name, group_path, parent_group_recid FROM user_group_info WHERE 1=1
AND parent_group_recid = #{groupRecid}
UNION ALL SELECT A.recid, A.group_name, A.group_path, A.parent_group_recid FROM user_group_info A JOIN result_sub_group B ON A.parent_group_recid = B.recid ) SELECT * FROM ( SELECT A.recid, A.group_name, A.group_path, A.parent_group_recid FROM result_sub_group A
INNER JOIN user_group_relation UGR ON A.recid = UGR.group_recid INNER JOIN user_info UI ON UGR.user_recid = UI.recid AND UI.user_level_cd = #{userLevelCd}
UNION ALL SELECT B.recid, B.group_name, B.group_path, B.parent_group_recid FROM user_group_info B
INNER JOIN user_group_relation UGR ON B.recid = UGR.group_recid INNER JOIN user_info UI ON UGR.user_recid = UI.recid AND UI.user_level_cd = #{userLevelCd}
WHERE B.recid = #{groupRecid}
) N ORDER BY N.recid ASC

WITH result_sub_group AS ( SELECT recid FROM user_group_info WHERE recid = #{grpId}
UNION ALL
SELECT A.recid FROM user_group_info A INNER JOIN result_sub_group B ON A.parent_group_recid = B.recid ) SELECT UI.user_id from user_info UI INNER JOIN user_group_relation UGR ON UGR.user_recid = UI.recid INNER JOIN result_sub_group B ON UGR.group_recid = B.recid

mapper.insert:
INSERT INTO user_group_relation(
	user_recid, group_recid,
	update_dt, update_ip, update_user
)
VALUES (
	#{userRecid}, #{groupRecid},
	LOCALTIMESTAMP, #{updateIp}, #{updateUser}
)

mapper.update:
UPDATE user_group_relation SET
	group_recid = #{groupRecid},
	update_dt = LOCALTIMESTAMP,
	update_ip =#{updateIp},
	update_user = #{updateUser}
WHERE user_recid = #{userRecid}

mapper.delete:
DELETE FROM user_group_relation
WHERE user_recid IN
#{id}
