mapper.select:
SELECT COUNT(*) FROM equip_account EA INNER JOIN equip_info EI ON EA.equip_recid = EI.recid INNER JOIN equip_group_relation EGR ON EGR.equip_recid = EI.recid INNER JOIN equip_group_info EG ON EGR.group_recid = EG.recid
AND EA.equip_recid = #{entity.equipRecid}
AND EA.account LIKE '%' + #{entity.account} + '%'
AND EI.equip_name LIKE '%' + #{extraParams.equipName} + '%'
AND EI.ip_primary LIKE '%' + #{extraParams.ipPrimary} + '%'
AND EI.vendor_name LIKE '%' + #{extraParams.vendorName} + '%'
AND EI.model_no LIKE '%' + #{extraParams.modelNo} + '%'
AND EI.equip_type = #{extraParams.equipType}
#{id}
