mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY ${sort.sortFieldNm} ${sort.sortDir}) AS rownum, recid, os_name, architecture, version, create_dt, create_ip, create_user, update_dt, update_ip, update_user, os_type FROM os_info
AND os_name LIKE '%' + #{entity.osName} + '%'
AND os_type = #{entity.osType}
AND CONVERT(date, create_dt) BETWEEN CONVERT(date, #{extraParams.startDate}) AND CONVERT(date, #{extraParams.endDate})
AND CONVERT(date, create_dt) >= CONVERT(date, #{extraParams.startDate})
AND CONVERT(date, #{extraParams.endDate}) >= CONVERT(date, create_dt)
) N
N.rownum BETWEEN #{page.startPage} AND #{page.endPage}

mapper.delete:
DELETE FROM os_info
WHERE recid IN

	#{id}
